        public static async Task<Guid> CreateNetwork(RestImplementation restImpl)
        {
            string cx = await Utils.GetUser1NDEx(restImpl)
                .Network()
                .GetComplete(SharedIds.NetworkId1);
            
            RestResponse response = await Utils.GetUser1NDEx(restImpl)
                .Network()
                .Create(null, cx);
            
            Assert.NotNull(response);
            Assert.True(response.wasSuccess);
            
            // network id from response
            return Utils.GetIdFromUrl(response.json);
        }

